 */
class HostedSmsAdapterTest extends \PHPUnit\Framework\TestCase {

    /**
     * @var SmsCenterAdapter
     */
    protected $object;

    private $config = [];
        
    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp(): void
    {
        $this->config = include  __DIR__ . '/../../config.php';
        $this->object = new HostedSmsAdapter();
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown(): void
    {
        
    }

    /**
     * @covers SmsZilla\Adapter\HostedSmsAdapter::send
     */
    public function testSend() {
        $message = new \SmsZilla\SmsMessageModel();
        $message->setText($this->config['message']);
        $message->addRecipient($this->config['my_phone']);
        $this->object->setParams([
            'login' => $this->config['hostedsms_login'],
            'password' => $this->config['hostedsms_password'],
            'sender' => $this->config['hostedsms_sender']]
        );
        $result = $this->object->send($message, false);
        $this->assertTrue($result);
        $this->assertCount(0, $this->object->getErrors());
    }
    
        
    /**
     * @covers SmsZilla\Adapter\HostedSmsAdapter::setParams
     */
    public function testSetParams() {
        $this->object->setParams([
            'login' => $this->config['hostedsms_login'],
            'password' => $this->config['hostedsms_password'],
            'sender' => $this->config['hostedsms_sender']]
        );
        $this->assertEquals($this->object->getParam('login'), $this->config['hostedsms_login']);
        
        $this->expectException(\SmsZilla\ConfigurationException::class);
        $this->object->setParams(['dummy' => 1]);
    }

}
