<?php
namespace SmsZilla\Adapter;

/**
 * Generated by PHPUnit_SkeletonGenerator on 2016-08-02 at 10:02:11.
 */
class SmsApiAdapterTest extends \PHPUnit\Framework\TestCase
{
    /**
     * @var SmsApiAdapter
     */
    protected $object;

    private $config = [];

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp(): void
    {
        $this->config = include  __DIR__ . '/../../config.php';
        $this->object = new SmsApiAdapter();
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown(): void
    {
    }

    /**
     * @covers SmsZilla\Adapter\SmsApiAdapter::send
     * @covers \SmsZilla\ConfigurationException::__construct
     */
    public function testTokenConfigError() {
        $this->expectExceptionMessage("SmsZilla\Adapter\SmsApiAdapter is not configured properly. Please set \"token\" or \"login\" and \"passwd_pash\" parameters properly.");
        $this->expectException(\SmsZilla\ConfigurationException::class);
        // store_path is not set
        $this->object->send(new \SmsZilla\SmsMessageModel);
    }


    /**
     * @covers SmsZilla\Adapter\SmsApiAdapter::send
     * @covers SmsZilla\Adapter\AbstractAdapter::getParams
     * @covers \SmsZilla\ConfigurationException::__construct
     */
    public function testSenderConfigError() {
        $this->expectExceptionMessage("SmsZilla\Adapter\SmsApiAdapter is not configured properly. Please set \"sender\" parameter properly.");
        $this->expectException(\SmsZilla\ConfigurationException::class);
        $this->object->setParams(['token' => $this->config['smsapi_token']]);
        $this->object->setParams(['sender' => '']);
        $this->object->send(new \SmsZilla\SmsMessageModel);
    }

    /**
     * @covers SmsZilla\Adapter\SmsApiAdapter::send
     * @covers SmsZilla\Adapter\AbstractAdapter::getErrors
     * @covers \SmsZilla\SendingError::__construct
     */
    public function testSend() {
        $message = new \SmsZilla\SmsMessageModel();
        $message->setText($this->config['message']);
        $message->addRecipient($this->config['my_phone']);
        $this->object->setParams(['token' => $this->config['smsapi_token']]);
        $result = $this->object->send($message);
        $this->assertTrue($result);
        $this->assertCount(0, $this->object->getErrors());
    }

    /**
     * @covers SmsZilla\Adapter\AbstractAdapter::setParams
     */
    public function testSetParams() {

        $this->object->setParams(['token' => $this->config['smsapi_token']]);
        $this->assertEquals($this->object->getParam('token'), $this->config['smsapi_token']);

        $this->expectException(\SmsZilla\ConfigurationException::class);
        $this->object->setParams(['dummy' => 1]);
    }
}
